definition.script:
pipeline {
    agent none
    stages {
        stage ('build') {
            agent {label 'build'}
            steps {
                 sh 'pwd'
                 sh 'rm -rf automation-scripts '
                 sh 'git clone https://github.com/kevalohith/automation-scripts.git'
                 sh 'sh /home/sone/workspace/pipelinejob/automation-scripts/maven.sh'
                 sh 'scp -r /home/sone/workspace/pipelinejob/hello-world-war/target/hello-world-war-1.0.0.war root@172.31.37.70:/opt/tomcat/webapps'
            }
        }
        stage ('deploy') {
            agent {label 'deploy'}
                steps{
                   sh 'sudo sh /opt/tomcat/bin/startup.sh'
                }
            }
        }
    }
